import React from "react";
import { useTheme } from "../context/ThemeContext";

export default function TrainingLogRow({ row, onDelete, onChange, isLast }) {
    const { theme } = useTheme();

    const handleChange = (field, value) => {
        onChange(row.id, field, value);
    };

    return (
        <div style={{
            display: "grid",
            gridTemplateColumns: "2fr 1fr 1fr 1fr 2fr 50px",
            gap: "1rem",
            padding: "1rem",
            background: theme.surface,
            borderBottom: isLast ? "none" : `1px solid ${theme.borderLight}`,
            transition: "background-color 0.3s ease, border-color 0.3s ease"
        }}>
            {/* Exercise */}
            <input
                type="text"
                value={row.exercise || ""}
                onChange={(e) => handleChange("exercise", e.target.value)}
                placeholder="Exercise name"
                style={{
                    background: theme.inputBackground,
                    color: theme.text,
                    border: `1px solid ${theme.inputBorder}`,
                    borderRadius: "6px",
                    padding: "0.5rem",
                    fontSize: "0.9rem",
                    transition: "background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease"
                }}
            />

            {/* Sets */}
            <input
                type="text"
                value={row.sets || ""}
                onChange={(e) => handleChange("sets", e.target.value)}
                placeholder="Sets"
                style={{
                    background: theme.inputBackground,
                    color: theme.text,
                    border: `1px solid ${theme.inputBorder}`,
                    borderRadius: "6px",
                    padding: "0.5rem",
                    fontSize: "0.9rem",
                    transition: "background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease"
                }}
            />

            {/* Reps */}
            <input
                type="text"
                value={row.reps || ""}
                onChange={(e) => handleChange("reps", e.target.value)}
                placeholder="Reps"
                style={{
                    background: theme.inputBackground,
                    color: theme.text,
                    border: `1px solid ${theme.inputBorder}`,
                    borderRadius: "6px",
                    padding: "0.5rem",
                    fontSize: "0.9rem",
                    transition: "background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease"
                }}
            />

            {/* Weight */}
            <input
                type="text"
                value={row.weight || ""}
                onChange={(e) => handleChange("weight", e.target.value)}
                placeholder="Weight"
                style={{
                    background: theme.inputBackground,
                    color: theme.text,
                    border: `1px solid ${theme.inputBorder}`,
                    borderRadius: "6px",
                    padding: "0.5rem",
                    fontSize: "0.9rem",
                    transition: "background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease"
                }}
            />

            {/* Notes */}
            <input
                type="text"
                value={row.notes || ""}
                onChange={(e) => handleChange("notes", e.target.value)}
                placeholder="Notes"
                style={{
                    background: theme.inputBackground,
                    color: theme.text,
                    border: `1px solid ${theme.inputBorder}`,
                    borderRadius: "6px",
                    padding: "0.5rem",
                    fontSize: "0.9rem",
                    transition: "background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease"
                }}
            />

            {/* Delete Button */}
            <button
                onClick={() => onDelete(row.id)}
                style={{
                    background: theme.surfaceSecondary,
                    color: theme.textSecondary,
                    border: `1px solid ${theme.border}`,
                    borderRadius: "6px",
                    padding: "0.5rem",
                    cursor: "pointer",
                    fontSize: "0.8rem",
                    fontWeight: "600",
                    transition: "background 0.2s ease, border-color 0.2s ease"
                }}
                onMouseOver={e => {
                    e.currentTarget.style.background = theme.surfaceTertiary;
                    e.currentTarget.style.borderColor = theme.textMuted;
                }}
                onMouseOut={e => {
                    e.currentTarget.style.background = theme.surfaceSecondary;
                    e.currentTarget.style.borderColor = theme.border;
                }}
            >
                Ã—
            </button>
        </div>
    );
}
