import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useTheme } from "../context/ThemeContext";
import SavedTablesPage from "../components/SavedTablesPage";
import TrainingLogManager from "../components/TrainingLogManager";
import Logo from "../components/Logo";
import ImportPanel from "../components/ImportPanel";

const manager = new TrainingLogManager();

export default function Dashboard() {
  const navigate = useNavigate();
  const { theme } = useTheme();
  const [isHoveringNewLog, setIsHoveringNewLog] = useState(false);
  const [showTopButtons, setShowTopButtons] = useState(true);
  const [lastScrollY, setLastScrollY] = useState(0);

  // Scroll detection for top buttons
  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      
      // Show buttons if at top of page or scrolling up
      const isAtTop = currentScrollY < 50;
      const isScrollingUp = currentScrollY < lastScrollY;
      
      setShowTopButtons(isAtTop || isScrollingUp);
      setLastScrollY(currentScrollY);
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, [lastScrollY]);

  const handleOpenSaved = () => {
    navigate("/log");
  };

  const handleNewLog = async () => {
    const newTable = manager.createNewTable();
    await manager.createTable(newTable);
    navigate(`/log/${newTable.id}`);
  };

  const handleSettings = () => {
    navigate("/settings");
  };

  const handleAICoaching = () => {
    navigate("/ai-coaching");
  };

  return (
    <div style={{ 
      background: theme.background, 
      minHeight: "100vh", 
      color: theme.text, 
      padding: "2rem",
      paddingTop: "5rem",
      position: "relative",
      transition: "background-color 0.3s ease, color 0.3s ease"
    }}>
      {/* Import Data Panel */}
      <ImportPanel onClick={() => navigate("/import-data")} />

      {/* Hamburger Menu */}
      <button
        onClick={handleSettings}
        style={{
          position: "fixed",
          top: "1rem",
          left: "1rem",
          background: theme.accentSecondary,
          color: theme.accent,
          padding: "0.7rem",
          border: "none",
          borderRadius: "10px",
          fontWeight: "600",
          fontSize: "1rem",
          cursor: "pointer",
          zIndex: 1000,
          transition: "all 0.3s ease",
          width: "50px",
          height: "50px",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          gap: "4px",
          opacity: showTopButtons ? 1 : 0,
          transform: showTopButtons ? "translateY(0)" : "translateY(-10px)",
          pointerEvents: showTopButtons ? "auto" : "none"
        }}
        onMouseOver={e => e.currentTarget.style.background = theme.accentHover}
        onMouseOut={e => e.currentTarget.style.background = theme.accentSecondary}
      >
        <div style={{
          width: "20px",
          height: "2px",
          background: theme.accent,
          borderRadius: "1px",
          transition: "background 0.2s ease"
        }} />
        <div style={{
          width: "20px",
          height: "2px",
          background: theme.accent,
          borderRadius: "1px",
          transition: "background 0.2s ease"
        }} />
        <div style={{
          width: "20px",
          height: "2px",
          background: theme.accent,
          borderRadius: "1px",
          transition: "background 0.2s ease"
        }} />
      </button>

      {/* AI Coaching Button */}
      <button
        onClick={handleAICoaching}
        style={{
          position: "fixed",
          top: "1rem",
          right: "8rem",
          background: theme.accentSecondary,
          color: theme.accent,
          padding: "0.7rem 1.4rem",
          border: "none",
          borderRadius: "10px",
          fontWeight: "600",
          fontSize: "1rem",
          cursor: "pointer",
          zIndex: 1000,
          transition: "all 0.3s ease",
          minWidth: "120px",
          opacity: showTopButtons ? 1 : 0,
          transform: showTopButtons ? "translateY(0)" : "translateY(-10px)",
          pointerEvents: showTopButtons ? "auto" : "none"
        }}
        onMouseOver={e => e.currentTarget.style.background = theme.accentHover}
        onMouseOut={e => e.currentTarget.style.background = theme.accentSecondary}
      >
        🤖 AI Coach
      </button>

      {/* Settings Button */}
      <button
        onClick={handleSettings}
        style={{
          position: "fixed",
          top: "1rem",
          right: "1rem",
          background: theme.surfaceSecondary,
          color: theme.textSecondary,
          padding: "0.7rem 1.4rem",
          border: `1px solid ${theme.border}`,
          borderRadius: "10px",
          fontWeight: "600",
          fontSize: "1rem",
          cursor: "pointer",
          zIndex: 1000,
          transition: "all 0.3s ease",
          minWidth: "100px",
          opacity: showTopButtons ? 1 : 0,
          transform: showTopButtons ? "translateY(0)" : "translateY(-10px)",
          pointerEvents: showTopButtons ? "auto" : "none"
        }}
        onMouseOver={e => {
          e.currentTarget.style.background = theme.surfaceTertiary;
          e.currentTarget.style.borderColor = theme.accent;
        }}
        onMouseOut={e => {
          e.currentTarget.style.background = theme.surfaceSecondary;
          e.currentTarget.style.borderColor = theme.border;
        }}
      >
        Settings
      </button>

      <div style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        marginBottom: "2rem",
        gap: "1rem"
      }}>
        <div style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          width: "60px",
          height: "60px",
          background: `linear-gradient(135deg, ${theme.accent}, ${theme.accentHover})`,
          borderRadius: "16px",
          boxShadow: "0 8px 20px rgba(0, 0, 0, 0.2)",
          transition: "transform 0.3s ease, box-shadow 0.3s ease"
        }}
        onMouseEnter={(e) => {
          e.currentTarget.style.transform = "scale(1.05)";
          e.currentTarget.style.boxShadow = "0 12px 25px rgba(0, 0, 0, 0.3)";
        }}
        onMouseLeave={(e) => {
          e.currentTarget.style.transform = "scale(1)";
          e.currentTarget.style.boxShadow = "0 8px 20px rgba(0, 0, 0, 0.2)";
        }}
        >
          <Logo />
        </div>
        <div style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "flex-start"
        }}>
          <h1 style={{ 
            fontSize: "2.2rem", 
            margin: 0,
            color: theme.accent,
            fontWeight: "700",
            transition: "color 0.3s ease"
          }}>
            SetPad
          </h1>
          <p style={{
            fontSize: "1rem",
            margin: 0,
            color: theme.textSecondary,
            fontWeight: "500",
            transition: "color 0.3s ease"
          }}>
            Your Fitness Journey
          </p>
        </div>
      </div>

      {/* Centered New Log Button */}
      <div style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        marginBottom: "3rem"
      }}>
        <div style={{
          position: "relative",
          width: "200px",
          height: "70px",
          background: `linear-gradient(to top, ${theme.accentSecondary}, ${theme.accent}, ${theme.accentHover})`,
          borderRadius: "50px",
          border: "none",
          outline: "none",
          cursor: "pointer",
          boxShadow: "0 15px 30px rgba(0, 0, 0, 0.3)",
          overflow: "hidden",
          transition: "transform 0.3s ease"
        }}
        onMouseEnter={() => setIsHoveringNewLog(true)}
        onMouseLeave={() => setIsHoveringNewLog(false)}
        onClick={handleNewLog}
        >
          <span style={{
            position: "absolute",
            width: "100%",
            top: isHoveringNewLog ? "-100%" : "50%",
            left: 0,
            transform: "translateY(-50%)",
            fontSize: "16px",
            textTransform: "uppercase",
            letterSpacing: "1px",
            color: "#fff",
            fontWeight: "600",
            textAlign: "center",
            transition: "top 0.5s ease"
          }}>
            New Log
          </span>
          <span style={{
            position: "absolute",
            width: "100%",
            top: isHoveringNewLog ? "50%" : "150%",
            left: 0,
            transform: "translateY(-50%)",
            fontSize: "16px",
            textTransform: "uppercase",
            letterSpacing: "1px",
            color: "#fff",
            fontWeight: "600",
            textAlign: "center",
            transition: "top 0.5s ease"
          }}>
            Let's Begin
          </span>
        </div>
      </div>

      <div
        onClick={handleOpenSaved}
        style={{
          overflow: "hidden",
          borderRadius: "1rem",
          border: `2px solid ${theme.accent}`,
          position: "relative",
          cursor: "pointer",
          transition: "transform 0.3s ease, border-color 0.3s ease",
          marginBottom: "2rem"
        }}
        onMouseEnter={(e) => (e.currentTarget.style.transform = "scale(1.02)")}
        onMouseLeave={(e) => (e.currentTarget.style.transform = "scale(1)")}
      >
        <div style={{ 
          pointerEvents: "none", 
          opacity: 0.8, 
          maxHeight: "400px", 
          overflow: "hidden" 
        }}>
          <SavedTablesPage previewMode={true} />
        </div>
        <div
          style={{
            position: "absolute",
            top: "1rem",
            left: "1rem",
            background: theme.background,
            color: theme.accent,
            fontWeight: "bold",
            padding: "0.5rem 1rem",
            borderRadius: "8px",
            fontSize: "0.9rem",
            transition: "background-color 0.3s ease, color 0.3s ease"
          }}
        >
          Open Saved Logs →
        </div>
      </div>

      <p style={{ 
        marginTop: "1rem", 
        opacity: 0.7,
        fontSize: "1.1rem",
        lineHeight: "1.6",
        color: theme.textSecondary,
        transition: "color 0.3s ease",
        textAlign: "center"
      }}>
        This space will soon show your analytics, recent logs, and suggested training insights.
      </p>
    </div>
  );
}
