import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import TrainingLogManager from "./TrainingLogManager";
import { getFirebaseUserAndToken } from "../utils/auth";

export default function SavedTablesPage({ previewMode = false }) {
    const navigate = useNavigate();
    const [tables, setTables] = useState([]);
    const [manager, setManager] = useState(null);

    useEffect(() => {
        async function setup() {
            const session = await getFirebaseUserAndToken();
            if (!session) return;
            const managerInstance = new TrainingLogManager(session.token);
            setManager(managerInstance);
            const data = await managerInstance.listTables();
            setTables(data);
        }
        setup();
    }, []);

    const handleNewTable = async () => {
        const today = new Date().toISOString().split("T")[0];
        const newId = `${Date.now()}`;
        const newTable = {
            id: newId,
            tableName: `New Table - ${today}`,
            date: today,
            rows: []
        };
        await manager.saveTable(newTable);
        navigate(`/log/${newId}`);
    };

    const handleOpenTable = (id) => navigate(`/log/${id}`);

    const handleDeleteTable = async (id) => {
        if (!window.confirm("Are you sure?")) return;
        await manager.deleteTable(id);
        setTables((prev) => prev.filter((t) => t.id !== id));
    };

    if (!manager) return <div style={{ background: "#000", minHeight: "100vh" }}></div>;

    return (
        <div style={{ padding: "2rem", background: "#000", minHeight: "100vh", color: "#f5f6fa" }}>
            {!previewMode && (
                <>
                    <button
                        onClick={() => navigate("/")}
                        style={{
                            position: "absolute",
                            top: "2rem",
                            left: "2rem",
                            background: "#31363f",
                            color: "#ffd966",
                            padding: "0.7rem 1.4rem",
                            border: "none",
                            borderRadius: "10px",
                            fontWeight: "600",
                            fontSize: "1rem",
                            cursor: "pointer",
                            zIndex: 10,
                            transition: "background 0.2s"
                        }}
                        onMouseOver={e => e.currentTarget.style.background = "#444950"}
                        onMouseOut={e => e.currentTarget.style.background = "#31363f"}
                    >
                        ← Back to Dashboard
                    </button>
                    <h1 style={{ fontSize: "2rem", marginBottom: "1.5rem" }}>Saved Logs</h1>
                    <button
                        onClick={handleNewTable}
                        style={{
                            background: "#31363f",
                            color: "#ffd966",
                            padding: "0.8rem 1.6rem",
                            border: "none",
                            borderRadius: "10px",
                            fontWeight: "600",
                            fontSize: "1rem",
                            cursor: "pointer",
                            marginBottom: "2rem"
                        }}
                    >
                        + New Table
                    </button>
                </>
            )}
            {tables.length === 0 ? (
                <p style={{ opacity: 0.7 }}>No saved tables yet.</p>
            ) : (
                <ul style={{ listStyle: "none", padding: 0 }}>
                    {tables.map((table) => (
                        <li
                            key={table.id}
                            style={{
                                marginBottom: "1rem",
                                padding: "0.5rem 1rem",
                                background: "#1e1e1e",
                                borderRadius: "8px",
                                display: "flex",
                                justifyContent: "space-between",
                                alignItems: "center",
                                cursor: "pointer"
                            }}
                            onClick={() => handleOpenTable(table.id)}
                        >
                            <div>
                                <strong>{table.tableName}</strong> — <em>{table.date}</em>
                            </div>
                            {!previewMode && (
                                <button
                                    onClick={e => {
                                        e.stopPropagation();
                                        handleDeleteTable(table.id);
                                    }}
                                    style={{
                                        background: "#ff4d4f",
                                        color: "#fff",
                                        border: "none",
                                        borderRadius: "6px",
                                        padding: "0.4rem 0.8rem",
                                        cursor: "pointer"
                                    }}
                                >
                                    Delete
                                </button>
                            )}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}
