import React, { useState, useEffect } from 'react';
import { useTheme } from '../context/ThemeContext';

const ProgressChart = ({ data, title, type = 'weight', height = 200 }) => {
    const { theme } = useTheme();
    const [hoveredPoint, setHoveredPoint] = useState(null);

    if (!data || data.length === 0) {
        return (
            <div style={{
                height,
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                background: theme.surfaceSecondary,
                borderRadius: '12px',
                border: `1px solid ${theme.border}`,
                color: theme.textSecondary
            }}>
                No data available for {title}
            </div>
        );
    }

    // Prepare data points with better scaling
    const values = data.map(d => parseFloat(d.value) || 0);
    const maxValue = Math.max(...values);
    const minValue = Math.min(...values);
    const valueRange = maxValue - minValue || 1;

    // Add padding to value range for better visualization
    const paddedMax = maxValue + (valueRange * 0.1);
    const paddedMin = Math.max(0, minValue - (valueRange * 0.1));
    const paddedRange = paddedMax - paddedMin;

    const padding = 50;
    const chartWidth = 500;
    const chartHeight = height - padding * 2;

    // Generate SVG path with better scaling
    const points = data.map((d, index) => {
        const x = padding + (index / (data.length - 1)) * (chartWidth - padding * 2);
        const y = padding + chartHeight - ((parseFloat(d.value) - paddedMin) / paddedRange) * chartHeight;
        return { 
            x, 
            y, 
            value: d.value, 
            date: d.date, 
            weight: d.weight,
            reps: d.reps,
            sets: d.sets,
            index 
        };
    });

    const pathD = points.reduce((path, point, index) => {
        return path + (index === 0 ? `M ${point.x} ${point.y}` : ` L ${point.x} ${point.y}`);
    }, '');

    // Generate area path for gradient fill
    const areaD = pathD + ` L ${points[points.length - 1].x} ${padding + chartHeight} L ${points[0].x} ${padding + chartHeight} Z`;

    return (
        <div style={{
            background: theme.cardBackground,
            borderRadius: '16px',
            padding: '1.5rem',
            border: `1px solid ${theme.cardBorder}`,
            position: 'relative'
        }}>
            <h3 style={{
                margin: '0 0 1rem 0',
                color: theme.accent,
                fontSize: '1.1rem',
                fontWeight: '600'
            }}>
                {title}
            </h3>

            <div style={{ position: 'relative', overflow: 'hidden', borderRadius: '8px' }}>
                <svg
                    width="100%"
                    height={height}
                    viewBox={`0 0 ${chartWidth} ${height}`}
                    style={{ display: 'block' }}
                >
                    {/* Grid lines */}
                    <defs>
                        <linearGradient id={`gradient-${title.replace(/\s+/g, '')}`} x1="0%" y1="0%" x2="0%" y2="100%">
                            <stop offset="0%" stopColor={theme.accent} stopOpacity="0.3" />
                            <stop offset="100%" stopColor={theme.accent} stopOpacity="0.05" />
                        </linearGradient>
                    </defs>

                    {/* Horizontal grid lines */}
                    {[0, 0.25, 0.5, 0.75, 1].map(ratio => {
                        const y = padding + chartHeight * ratio;
                        return (
                            <line
                                key={ratio}
                                x1={padding}
                                y1={y}
                                x2={chartWidth - padding}
                                y2={y}
                                stroke={theme.border}
                                strokeWidth="1"
                                opacity="0.2"
                                strokeDasharray="2,2"
                            />
                        );
                    })}

                    {/* Vertical grid lines */}
                    {points.map((point, index) => {
                        if (index % Math.ceil(points.length / 5) === 0) {
                            return (
                                <line
                                    key={`vertical-${index}`}
                                    x1={point.x}
                                    y1={padding}
                                    x2={point.x}
                                    y2={padding + chartHeight}
                                    stroke={theme.border}
                                    strokeWidth="1"
                                    opacity="0.1"
                                    strokeDasharray="2,2"
                                />
                            );
                        }
                        return null;
                    })}

                    {/* Area fill */}
                    <path
                        d={areaD}
                        fill={`url(#gradient-${title.replace(/\s+/g, '')})`}
                        stroke="none"
                    />

                    {/* Main line */}
                    <path
                        d={pathD}
                        fill="none"
                        stroke={theme.accent}
                        strokeWidth="3"
                        strokeLinecap="round"
                        strokeLinejoin="round"
                    />

                    {/* Data points */}
                    {points.map((point) => (
                        <circle
                            key={point.index}
                            cx={point.x}
                            cy={point.y}
                            r={hoveredPoint === point.index ? "6" : "4"}
                            fill={theme.accent}
                            stroke={theme.background}
                            strokeWidth="2"
                            style={{
                                cursor: 'pointer',
                                transition: 'r 0.2s ease'
                            }}
                            onMouseEnter={() => setHoveredPoint(point.index)}
                            onMouseLeave={() => setHoveredPoint(null)}
                        />
                    ))}

                    {/* Y-axis labels */}
                    {[minValue, (minValue + maxValue) / 2, maxValue].map((value, index) => {
                        const y = padding + chartHeight - (index * chartHeight / 2);
                        return (
                            <text
                                key={value}
                                x={padding - 10}
                                y={y + 4}
                                textAnchor="end"
                                fontSize="12"
                                fill={theme.textSecondary}
                            >
                                {value.toFixed(1)}{type === 'weight' ? 'lbs' : ''}
                            </text>
                        );
                    })}
                </svg>

                {/* Tooltip */}
                {hoveredPoint !== null && (
                    <div style={{
                        position: 'absolute',
                        top: points[hoveredPoint].y - 60,
                        left: Math.min(points[hoveredPoint].x - 50, chartWidth - 120),
                        background: theme.surfaceTertiary,
                        border: `1px solid ${theme.border}`,
                        borderRadius: '8px',
                        padding: '0.5rem 0.75rem',
                        fontSize: '0.8rem',
                        color: theme.text,
                        boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
                        zIndex: 10,
                        minWidth: '100px',
                        textAlign: 'center'
                    }}>
                        <div style={{ fontWeight: '600', color: theme.accent }}>
                            {points[hoveredPoint].value}{type === 'weight' ? 'lbs' : ''}
                        </div>
                        <div style={{ color: theme.textSecondary, fontSize: '0.7rem' }}>
                            {new Date(points[hoveredPoint].date).toLocaleDateString()}
                        </div>
                    </div>
                )}
            </div>

            {/* Stats summary */}
            <div style={{
                display: 'flex',
                justifyContent: 'space-between',
                marginTop: '1rem',
                padding: '0.75rem',
                background: theme.surfaceSecondary,
                borderRadius: '8px',
                fontSize: '0.8rem'
            }}>
                <div style={{ textAlign: 'center' }}>
                    <div style={{ color: theme.textSecondary }}>Best</div>
                    <div style={{ color: theme.accent, fontWeight: '600' }}>
                        {maxValue.toFixed(1)}{type === 'weight' ? 'lbs' : ''}
                    </div>
                </div>
                <div style={{ textAlign: 'center' }}>
                    <div style={{ color: theme.textSecondary }}>Sessions</div>
                    <div style={{ color: theme.accent, fontWeight: '600' }}>
                        {data.length}
                    </div>
                </div>
                <div style={{ textAlign: 'center' }}>
                    <div style={{ color: theme.textSecondary }}>Change</div>
                    <div style={{ 
                        color: data[data.length - 1].value >= data[0].value ? '#28a745' : '#dc3545',
                        fontWeight: '600'
                    }}>
                        {data.length > 1 ? 
                            `${((data[data.length - 1].value - data[0].value) / data[0].value * 100).toFixed(1)}%` :
                            'N/A'
                        }
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ProgressChart;
