import React, { useState } from "react";
import { useTheme } from "../context/ThemeContext";

// Implement the API call to upload the file
async function importDataAPI(file) {
  const formData = new FormData();
  formData.append("file", file);
  try {
    const res = await fetch("http://localhost:8000/import-data", {
      method: "POST",
      body: formData,
    });
    if (!res.ok) {
      const error = await res.text();
      return { success: false, message: error || "Import failed." };
    }
    const data = await res.json();
    return { success: true, message: data.message || "Import successful!" };
  } catch (err) {
    return { success: false, message: "Network or server error." };
  }
}

const ImportDataPage = () => {
  const { theme } = useTheme();
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState("");

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setStatus("");
  };

  const handleImport = async () => {
    if (!file) {
      setStatus("Please select a file.");
      return;
    }
    setStatus("Importing...");
    try {
      const result = await importDataAPI(file);
      setStatus(result.message || (result.success ? "Import successful!" : "Import failed."));
    } catch (err) {
      setStatus("Error importing file.");
    }
  };

  return (
    <div style={{
      background: theme.background,
      minHeight: "100vh",
      color: theme.text,
      padding: "2rem",
      paddingTop: "5rem",
      transition: "background-color 0.3s, color 0.3s"
    }}>
      <h1 style={{ color: theme.accent, marginBottom: "2rem" }}>Import Data</h1>
      <div style={{
        background: theme.cardBackground,
        borderRadius: "16px",
        padding: "2rem",
        maxWidth: "400px",
        margin: "0 auto",
        boxShadow: theme.shadowLight,
        border: `1px solid ${theme.cardBorder}`
      }}>
        <label style={{ fontWeight: 600, color: theme.textSecondary }}>
          Select a file to import (CSV, TXT, Excel):
        </label>
        <input
          type="file"
          accept=".csv,.txt,application/vnd.openxmlformats-officedocument.spreadsheetml.sheet,application/vnd.ms-excel"
          onChange={handleFileChange}
          style={{ display: "block", margin: "1rem 0" }}
        />
        <button
          onClick={handleImport}
          style={{
            background: theme.accent,
            color: theme.background,
            border: "none",
            borderRadius: "8px",
            padding: "0.8rem 1.5rem",
            fontWeight: 600,
            fontSize: "1rem",
            cursor: "pointer",
            marginTop: "1rem"
          }}
        >
          Import
        </button>
        {status && (
          <div style={{ marginTop: "1rem", color: theme.textSecondary }}>{status}</div>
        )}
      </div>
    </div>
  );
};

export default ImportDataPage;
