import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useTheme } from "../context/ThemeContext";
import TrainingLogManager from "./TrainingLogManager";

const manager = new TrainingLogManager();

export default function SavedTablesPage({ previewMode = false }) {
    const navigate = useNavigate();
    const { theme } = useTheme();
    const [tables, setTables] = useState([]);
    const [isHoveringNewLog, setIsHoveringNewLog] = useState(false);
    const [isNavigating, setIsNavigating] = useState(false);

    useEffect(() => {
        const fetchTables = async () => {
            const result = await manager.listTables();
            setTables(result);
        };
        fetchTables();
    }, []);

    const handleNewTable = async () => {
        setIsNavigating(true);
        const newTable = manager.createNewTable();
        await manager.saveTable(newTable);
        setTimeout(() => {
            navigate(`/log/${newTable.id}`);
        }, 150);
    };

    const handleOpenTable = (id) => {
        setIsNavigating(true);
        setTimeout(() => {
            navigate(`/log/${id}`);
        }, 150);
    };

    const handleDeleteTable = async (id) => {
        if (window.confirm("Are you sure you want to delete this log?")) {
            await manager.deleteTable(id);
            setTables((prev) => prev.filter((table) => table.id !== id));
        }
    };

    const handleBackToDashboard = () => {
        setIsNavigating(true);
        setTimeout(() => {
            navigate("/");
        }, 150);
    };

    // Format date for display
    const formatDate = (dateString) => {
        if (!dateString) return "";
        const date = new Date(dateString);
        return date.toLocaleDateString('en-US', { 
            weekday: 'short', 
            year: 'numeric', 
            month: 'short', 
            day: 'numeric' 
        });
    };

    return (
        <div
            style={{
                padding: "2rem",
                paddingTop: previewMode ? "1rem" : "4rem",
                background: theme.background,
                minHeight: previewMode ? "auto" : "100vh",
                color: theme.text,
                position: "relative",
                transition: "all 0.3s ease",
                opacity: isNavigating ? 0.8 : 1,
                transform: isNavigating ? 'scale(0.98)' : 'scale(1)'
            }}
        >
            {/* Back to Dashboard Button */}
            {!previewMode && (
                <button
                    onClick={handleBackToDashboard}
                    style={{
                        position: "fixed",
                        top: "1rem",
                        left: "1rem",
                        background: theme.accentSecondary,
                        color: theme.accent,
                        padding: "0.7rem 1.4rem",
                        border: "none",
                        borderRadius: "10px",
                        fontWeight: "600",
                        fontSize: "1rem",
                        cursor: "pointer",
                        zIndex: 1000,
                        transition: "all 0.3s ease",
                        transform: "translateY(0)"
                    }}
                    onMouseOver={e => {
                        e.currentTarget.style.background = theme.accentHover;
                        e.currentTarget.style.transform = "translateY(-2px)";
                    }}
                    onMouseOut={e => {
                        e.currentTarget.style.background = theme.accentSecondary;
                        e.currentTarget.style.transform = "translateY(0)";
                    }}
                >
                    ← Back to Dashboard
                </button>
            )}

            {!previewMode && (
                <>
                    <h1 style={{ 
                        fontSize: "2.5rem", 
                        marginBottom: "2rem",
                        color: theme.accent,
                        textAlign: "center",
                        transition: "color 0.3s ease"
                    }}>
                        Saved Training Logs
                    </h1>
                    
                    {/* New Log Button with new style */}
                    <div style={{
                        display: "flex",
                        justifyContent: "center",
                        alignItems: "center",
                        marginBottom: "2rem"
                    }}>
                        <div style={{
                            position: "relative",
                            width: "160px",
                            height: "60px",
                            background: `linear-gradient(to top, ${theme.accentSecondary}, ${theme.accent}, ${theme.accentHover})`,
                            borderRadius: "50px",
                            border: "none",
                            outline: "none",
                            cursor: "pointer",
                            boxShadow: "0 15px 30px rgba(0, 0, 0, 0.3)",
                            overflow: "hidden",
                            transition: "all 0.3s ease",
                            transform: "translateY(0)"
                        }}
                        onMouseEnter={() => setIsHoveringNewLog(true)}
                        onMouseLeave={() => setIsHoveringNewLog(false)}
                        onClick={handleNewTable}
                        onMouseOver={e => {
                            e.currentTarget.style.transform = "translateY(-3px) scale(1.03)";
                            e.currentTarget.style.boxShadow = "0 18px 35px rgba(0, 0, 0, 0.4)";
                        }}
                        onMouseOut={e => {
                            e.currentTarget.style.transform = "translateY(0) scale(1)";
                            e.currentTarget.style.boxShadow = "0 15px 30px rgba(0, 0, 0, 0.3)";
                        }}
                        >
                            <span style={{
                                position: "absolute",
                                width: "100%",
                                top: isHoveringNewLog ? "-100%" : "50%",
                                left: 0,
                                transform: "translateY(-50%)",
                                fontSize: "14px",
                                textTransform: "uppercase",
                                letterSpacing: "1px",
                                color: "#fff",
                                fontWeight: "600",
                                textAlign: "center",
                                transition: "top 0.5s ease"
                            }}>
                                + New Log
                            </span>
                            <span style={{
                                position: "absolute",
                                width: "100%",
                                top: isHoveringNewLog ? "50%" : "150%",
                                left: 0,
                                transform: "translateY(-50%)",
                                fontSize: "14px",
                                textTransform: "uppercase",
                                letterSpacing: "1px",
                                color: "#fff",
                                fontWeight: "600",
                                textAlign: "center",
                                transition: "top 0.5s ease"
                            }}>
                                Let's Begin
                            </span>
                        </div>
                    </div>
                </>
            )}

            {tables.length === 0 ? (
                <p style={{ 
                    opacity: 0.7,
                    fontSize: "1.1rem",
                    textAlign: "center",
                    marginTop: "2rem",
                    color: theme.textSecondary,
                    transition: "color 0.3s ease"
                }}>
                    No saved logs yet.
                </p>
            ) : (
                <ul style={{ listStyle: "none", padding: 0 }}>
                    {tables.map((table) => (
                        <li
                            key={table.id}
                            style={{
                                marginBottom: "1rem",
                                padding: "1.5rem",
                                background: theme.cardBackground,
                                borderRadius: "12px",
                                display: "flex",
                                justifyContent: "space-between",
                                alignItems: "center",
                                border: `1px solid ${theme.cardBorder}`,
                                transition: "all 0.3s ease",
                                transform: "translateY(0)",
                                cursor: "pointer"
                            }}
                            onMouseEnter={(e) => {
                                e.currentTarget.style.transform = "translateY(-3px)";
                                e.currentTarget.style.borderColor = theme.accent;
                                e.currentTarget.style.boxShadow = "0 8px 25px rgba(0, 0, 0, 0.15)";
                            }}
                            onMouseLeave={(e) => {
                                e.currentTarget.style.transform = "translateY(0)";
                                e.currentTarget.style.borderColor = theme.cardBorder;
                                e.currentTarget.style.boxShadow = "none";
                            }}
                            onClick={() => handleOpenTable(table.id)}
                        >
                            <div 
                                style={{ 
                                    flex: 1,
                                    paddingRight: "1rem"
                                }} 
                            >
                                <strong style={{ 
                                    fontSize: "1.2rem",
                                    color: theme.text,
                                    transition: "color 0.3s ease",
                                    display: "block",
                                    marginBottom: "0.5rem"
                                }}>
                                    {table.tableName}
                                </strong>
                                <div style={{
                                    display: "flex",
                                    alignItems: "center",
                                    gap: "1rem"
                                }}>
                                    <span style={{ 
                                        color: theme.accent,
                                        fontSize: "0.9rem",
                                        fontWeight: "600",
                                        transition: "color 0.3s ease"
                                    }}>
                                        📅 {formatDate(table.date)}
                                    </span>
                                    {table.lastOpened && (
                                        <span style={{ 
                                            opacity: 0.7,
                                            color: theme.textSecondary,
                                            fontSize: "0.8rem",
                                            transition: "color 0.3s ease"
                                        }}>
                                            Last opened: {formatDate(table.lastOpened)}
                                        </span>
                                    )}
                                </div>
                            </div>
                            {!previewMode && (
                                <button
                                    onClick={(e) => {
                                        e.stopPropagation();
                                        handleDeleteTable(table.id);
                                    }}
                                    style={{
                                        background: theme.surfaceSecondary,
                                        color: theme.textSecondary,
                                        border: `1px solid ${theme.border}`,
                                        borderRadius: "8px",
                                        padding: "0.5rem 1rem",
                                        cursor: "pointer",
                                        fontWeight: "600",
                                        transition: "all 0.3s ease",
                                        transform: "translateY(0)"
                                    }}
                                    onMouseOver={e => {
                                        e.currentTarget.style.background = theme.surfaceTertiary;
                                        e.currentTarget.style.borderColor = theme.textMuted;
                                        e.currentTarget.style.transform = "translateY(-1px)";
                                    }}
                                    onMouseOut={e => {
                                        e.currentTarget.style.background = theme.surfaceSecondary;
                                        e.currentTarget.style.borderColor = theme.border;
                                        e.currentTarget.style.transform = "translateY(0)";
                                    }}
                                >
                                    Delete
                                </button>
                            )}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}
