const manager = new TrainingLogManager();

export default function TrainingLogRow({ rowData, onUpdate }) {
    const [muscleGroup, setMuscleGroup] = useState(rowData.muscleGroup);
    const [exercise, setExercise] = useState(rowData.exercise);
    const [sets, setSets] = useState(rowData.sets); // <-- FIXED: do not wrap in []
    const [notes, setNotes] = useState(rowData.notes);
    const [showNotes, setShowNotes] = useState(rowData.showNotes);
    const [weightUnit, setWeightUnit] = useState(rowData.weightUnit);
    const [showSuggestions, setShowSuggestions] = useState(false);
    const [previousData, setPreviousData] = useState(null);
    const [isLoading, setIsLoading] = useState(false);

    const handleSetChange = (index, field, value) => {
        setSets(sets =>
            sets.map((set, i) =>
                i === index ? { ...set, [field]: value } : set
            )
        );
    };

    const handleWeightKeyDown = (e, index) => {
        // Optionally, you can add logic here if you want to handle Enter key
    };

    const toggleWeightUnit = () => {
        const newUnit = weightUnit === "lbs" ? "kg" : "lbs";
        const convertedSets = sets.map(set => {
            let weight = set.weight;
            if (weight !== '') {
                weight = parseFloat(weight);
                if (!isNaN(weight)) {
                    weight = newUnit === "kg"
                        ? (weight / 2.20462).toFixed(2)
                        : (weight * 2.20462).toFixed(2);
                }
            }
            return { ...set, weight: weight };
        });
        setSets(convertedSets);
        setWeightUnit(newUnit);
    };

    const addSet = () => {
        setSets([...sets, { reps: '', weight: '' }]);
    };

    const removeSet = (index) => {
        if (sets.length > 1) {
            setSets(sets.filter((_, i) => i !== index));
        }
    };

    const fetchPreviousExerciseData = async () => {
        if (!exercise.trim()) return;
        
        setIsLoading(true);
        try {
            const allTables = await manager.listTables();
            let previousExercises = [];
            
            // Search through all tables for this exercise
            for (const table of allTables) {
                const tableData = await manager.loadTable(table.id);
                if (tableData && tableData.rows) {
                    const matchingRows = tableData.rows.filter(row => 
                        row.exercise && row.exercise.toLowerCase() === exercise.toLowerCase()
                    );
                    
                    matchingRows.forEach(row => {
                        if (row.sets && row.sets.length > 0) {
                            previousExercises.push({
                                date: tableData.date,
                                tableName: tableData.tableName,
                                sets: row.sets,
                                notes: row.notes,
                                weightUnit: row.weightUnit
                            });
                        }
                    });
                }
            }
            
            // Sort by date (most recent first)
            previousExercises.sort((a, b) => new Date(b.date) - new Date(a.date));
            
            setPreviousData(previousExercises.length > 0 ? previousExercises[0] : null);
        } catch (error) {
            console.error("Error fetching previous exercise data:", error);
            setPreviousData(null);
        } finally {
            setIsLoading(false);
        }
    };

    const handleSuggestionToggle = () => {
        if (!showSuggestions && exercise.trim()) {
            fetchPreviousExerciseData();
        }
        setShowSuggestions(!showSuggestions);
    };

    const applyPreviousData = () => {
        if (previousData && previousData.sets) {
            setSets(previousData.sets);
            setWeightUnit(previousData.weightUnit);
            if (previousData.notes) {
                setNotes(previousData.notes);
                setShowNotes(true);
            }
            setShowSuggestions(false);
        }
    };

    useEffect(() => {
        onUpdate({
            ...rowData,
            muscleGroup,
            exercise,
            sets,
            notes,
            showNotes,
            weightUnit,
        });
    }, [muscleGroup, exercise, sets, notes, showNotes, weightUnit]);

    // Sync state with rowData when it changes (e.g. on reload)
    useEffect(() => {
        setMuscleGroup(rowData.muscleGroup);
        setExercise(rowData.exercise);
        setSets(rowData.sets);
        setNotes(rowData.notes);
        setShowNotes(rowData.showNotes);
        setWeightUnit(rowData.weightUnit);
    }, [rowData]);

    // Dark mode & Apple Notes-like style variables
    const darkBg = "#23272f";
    const darkCard = "#282c34";
    const borderColor = "#444950";
    const labelColor = "#bfc7d5";
    const inputBg = "#23272f";
    const inputText = "#f5f6fa";
    const accent = "#ffd966";
    const buttonBg = "#31363f";
    const buttonText = "#ffd966";
    const buttonHover = "#444950";
    const disabledBg = "#1a1d23";
    const disabledText = "#666";

    const hasExerciseData = exercise.trim().length > 0;

    return (
        <div
            style={{
                marginBottom: "2rem",
                padding: "1.5rem",
                background: darkCard,
                border: `1px solid ${borderColor}`,
                borderRadius: "16px",
                boxShadow: "0 4px 16px rgba(0,0,0,0.25)",
                fontFamily: "'Segoe UI', 'San Francisco', 'Arial', sans-serif",
                color: inputText,
                transition: "background 0.3s, color 0.3s",
                position: "relative"
            }}
        >
            {/* Muscle Group */}
            <div style={{ marginBottom: "1rem" }}>
                <label style={{ fontWeight: "600", color: labelColor }}>Muscle Group:</label><br />
                <input
                    type="text"
                    value={muscleGroup}
                    onChange={(e) => setMuscleGroup(e.target.value)}
                    placeholder="Enter Exersice Focus Here"
                    style={{
                        padding: "0.5rem",
                        width: "100%",
                        borderRadius: "8px",
                        border: `1px solid ${borderColor}`,
                        background: inputBg,
                        color: inputText,
                        fontSize: "1rem",
                        marginTop: "0.2rem"
                    }}
                />
            </div>

            {/* Exercise Name */}
            <div style={{ marginBottom: "1rem" }}>
                <label style={{ fontWeight: "600", color: labelColor }}>Exercise:</label><br />
                <input
                    type="text"
                    value={exercise}
                    onChange={(e) => setExercise(e.target.value)}
                    placeholder="Name of exersice"
                    style={{
                        padding: "0.5rem",
                        width: "100%",
                        borderRadius: "8px",
                        border: `1px solid ${borderColor}`,
                        background: inputBg,
                        color: inputText,
                        fontSize: "1rem",
                        marginTop: "0.2rem"
                    }}
                />
            </div>

            {/* Smart Suggestion Button */}
            <div style={{ marginBottom: "1rem" }}>
                <button
                    onClick={handleSuggestionToggle}
                    disabled={!hasExerciseData}
                    style={{
                        background: hasExerciseData ? buttonBg : disabledBg,
                        color: hasExerciseData ? buttonText : disabledText,
                        border: "none",
                        borderRadius: "8px",
                        padding: "0.5rem 1rem",
                        cursor: hasExerciseData ? "pointer" : "not-allowed",
                        fontWeight: "600",
                        fontSize: "0.9rem",
                        transition: "background 0.2s",
                        width: "100%",
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        gap: "0.5rem"
                    }}
                    onMouseOver={e => {
                        if (hasExerciseData) {
                            e.currentTarget.style.background = buttonHover;
                        }
                    }}
                    onMouseOut={e => {
                        if (hasExerciseData) {
                            e.currentTarget.style.background = buttonBg;
                        }
                    }}
                >
                    {isLoading ? (
                        <>
                            <span>⏳</span>
                            Loading...
                        </>
                    ) : (
                        <>
                            <span>💡</span>
                            {hasExerciseData ? "Show Previous Performance" : "Enter exercise name first"}
                        </>
                    )}
                </button>
            </div>

            {/* Suggestion Card Overlay */}
            {showSuggestions && (
                <div style={{
                    position: "fixed",
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    background: "rgba(0, 0, 0, 0.8)",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    zIndex: 1000,
                    padding: "1rem"
                }}
                onClick={() => setShowSuggestions(false)}
                >
                    <div style={{
                        background: darkCard,
                        borderRadius: "16px",
                        padding: "1.5rem",
                        maxWidth: "90vw",
                        width: "400px",
                        maxHeight: "80vh",
                        overflow: "auto",
                        border: `1px solid ${borderColor}`,
                        boxShadow: "0 8px 32px rgba(0,0,0,0.5)"
                    }}
                    onClick={(e) => e.stopPropagation()}
                    >
                        <div style={{
                            display: "flex",
                            justifyContent: "space-between",
                            alignItems: "center",
                            marginBottom: "1rem"
                        }}>
                            <h3 style={{ 
                                margin: 0, 
                                color: accent,
                                fontSize: "1.2rem"
                            }}>
                                Previous Performance
                            </h3>
                            <button
                                onClick={() => setShowSuggestions(false)}
                                style={{
                                    background: "none",
                                    border: "none",
                                    color: labelColor,
                                    fontSize: "1.5rem",
                                    cursor: "pointer",
                                    padding: "0.2rem"
                                }}
                            >
                                ×
                            </button>
                        </div>

                        {previousData ? (
                            <div>
                                <div style={{
                                    background: inputBg,
                                    padding: "1rem",
                                    borderRadius: "8px",
                                    marginBottom: "1rem"
                                }}>
                                    <div style={{ marginBottom: "0.5rem" }}>
                                        <strong style={{ color: accent }}>Date:</strong> {previousData.date}
                                    </div>
                                    <div style={{ marginBottom: "0.5rem" }}>
                                        <strong style={{ color: accent }}>Log:</strong> {previousData.tableName}
                                    </div>
                                    <div style={{ marginBottom: "1rem" }}>
                                        <strong style={{ color: accent }}>Sets:</strong>
                                    </div>
                                    {previousData.sets.map((set, index) => (
                                        <div key={index} style={{
                                            display: "flex",
                                            gap: "1rem",
                                            marginBottom: "0.5rem",
                                            padding: "0.5rem",
                                            background: darkBg,
                                            borderRadius: "6px"
                                        }}>
                                            <span style={{ color: labelColor }}>Set {index + 1}:</span>
                                            <span>{set.reps} reps</span>
                                            <span>{set.weight} {previousData.weightUnit}</span>
                                        </div>
                                    ))}
                                    {previousData.notes && (
                                        <div style={{ marginTop: "1rem" }}>
                                            <strong style={{ color: accent }}>Notes:</strong>
                                            <div style={{
                                                background: darkBg,
                                                padding: "0.5rem",
                                                borderRadius: "6px",
                                                marginTop: "0.5rem",
                                                fontStyle: "italic"
                                            }}>
                                                {previousData.notes}
                                            </div>
                                        </div>
                                    )}
                                </div>
                                <button
                                    onClick={applyPreviousData}
                                    style={{
                                        background: accent,
                                        color: darkBg,
                                        border: "none",
                                        borderRadius: "8px",
                                        padding: "0.8rem 1.5rem",
                                        cursor: "pointer",
                                        fontWeight: "600",
                                        width: "100%",
                                        fontSize: "1rem"
                                    }}
                                >
                                    Apply This Data
                                </button>
                            </div>
                        ) : (
                            <div style={{
                                textAlign: "center",
                                padding: "2rem",
                                color: labelColor
                            }}>
                                <div style={{ fontSize: "3rem", marginBottom: "1rem" }}>📝</div>
                                <p>No previous data found for "{exercise}"</p>
                                <p style={{ fontSize: "0.9rem", opacity: 0.7 }}>
                                    This might be a new exercise for you!
                                </p>
                            </div>
                        )}
                    </div>
                </div>
            )}

            {/* Sets */}
            <div style={{ marginBottom: "1rem" }}>
                <label style={{ fontWeight: "600", color: labelColor }}>Sets:</label>
                {sets.map((set, index) => (
                    <div key={index} style={{ display: "flex", alignItems: "center", marginBottom: "0.5rem" }}>
                        <input
                            type="number"
                            min="0"
                            placeholder="Reps"
                            value={set.reps}
                            onChange={(e) => {
                                const val = e.target.value;
                                if (/^\d*$/.test(val)) handleSetChange(index, "reps", val);
                            }}
                            style={{
                                padding: "0.5rem",
                                width: "80px",
                                marginRight: "0.5rem",
                                borderRadius: "8px",
                                border: `1px solid ${borderColor}`,
                                background: inputBg,
                                color: inputText
                            }}
                        />
                        <input
                            type="number"
                            placeholder="Weight"
                            value={set.weight}
                            onChange={(e) => handleSetChange(index, "weight", e.target.value)}
                            onKeyDown={(e) => handleWeightKeyDown(e, index)}
                            style={{
                                padding: "0.5rem",
                                width: "100px",
                                marginRight: "0.5rem",
                                borderRadius: "8px",
                                border: `1px solid ${borderColor}`,
                                background: inputBg,
                                color: inputText
                            }}
                        />
                        <span style={{ marginRight: "0.5rem", fontWeight: "500", color: accent }}>{weightUnit}</span>
                        {sets.length > 1 && (
                            <button
                                type="button"
                                onClick={() => removeSet(index)}
                                style={{
                                    background: buttonBg,
                                    color: buttonText,
                                    border: "none",
                                    borderRadius: "6px",
                                    padding: "0.3rem 0.8rem",
                                    cursor: "pointer",
                                    marginLeft: "0.2rem",
                                    transition: "background 0.2s"
                                }}
                                onMouseOver={e => e.currentTarget.style.background = buttonHover}
                                onMouseOut={e => e.currentTarget.style.background = buttonBg}
                            >
                                Remove
                            </button>
                        )}
                    </div>
                ))}
                <button
                    type="button"
                    onClick={addSet}
                    style={{
                        marginRight: "0.5rem",
                        background: buttonBg,
                        color: buttonText,
                        border: "none",
                        borderRadius: "6px",
                        padding: "0.4rem 0.9rem",
                        cursor: "pointer",
                        fontWeight: 600,
                        transition: "background 0.2s"
                    }}
                    onMouseOver={e => e.currentTarget.style.background = buttonHover}
                    onMouseOut={e => e.currentTarget.style.background = buttonBg}
                >
                    Add Set
                </button>
                <button
                    type="button"
                    onClick={toggleWeightUnit}
                    style={{
                        background: buttonBg,
                        color: buttonText,
                        border: "none",
                        borderRadius: "6px",
                        padding: "0.4rem 0.9rem",
                        cursor: "pointer",
                        fontWeight: 600,
                        transition: "background 0.2s"
                    }}
                    onMouseOver={e => e.currentTarget.style.background = buttonHover}
                    onMouseOut={e => e.currentTarget.style.background = buttonBg}
                >
                    Unit: {weightUnit.toUpperCase()}
                </button>
            </div>

            {/* Notes */}
            <div style={{ marginTop: "1rem" }}>
                <button
                    onClick={() => setShowNotes(!showNotes)}
                    style={{
                        background: buttonBg,
                        color: buttonText,
                        border: "none",
                        borderRadius: "6px",
                        padding: "0.5rem 1.2rem",
                        cursor: "pointer",
                        fontWeight: 600,
                        transition: "background 0.2s"
                    }}
                    onMouseOver={e => e.currentTarget.style.background = buttonHover}
                    onMouseOut={e => e.currentTarget.style.background = buttonBg}
                >
                    {showNotes ? "Hide Notes" : "Add Notes"}
                </button>
                {showNotes && (
                    <div>
                        <textarea
                            rows="3"
                            value={notes}
                            onChange={(e) => setNotes(e.target.value)}
                            placeholder="No notes"
                            style={{
                                width: "100%",
                                marginTop: "0.5rem",
                                borderRadius: "8px",
                                padding: "0.5rem",
                                border: `1px solid ${borderColor}`,
                                background: inputBg,
                                color: inputText,
                                fontSize: "1rem"
                            }}
                        />
                    </div>
                )}
            </div>
        </div>
    );
}
