import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useTheme } from "../context/ThemeContext";
import TrainingLogManager from "./TrainingLogManager";
import { SkeletonLoader } from "./LoadingSpinner";
import { DataExportService } from "../services/dataExport";

const manager = new TrainingLogManager();

export default function SavedTablesPage({ previewMode = false }) {
    const navigate = useNavigate();
    const { theme } = useTheme();
    const [tables, setTables] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [deleteModal, setDeleteModal] = useState({ isOpen: false, tableId: null, tableName: "" });
    const [windowWidth, setWindowWidth] = useState(window.innerWidth);

    useEffect(() => {
        const fetchTables = async () => {
            try {
                setIsLoading(true);
                const result = await manager.listTables();
                setTables(result);
            } catch (error) {
                console.error("Error fetching tables:", error);
            } finally {
                setIsLoading(false);
            }
        };
        fetchTables();
    }, []);

    useEffect(() => {
        const handleResize = () => setWindowWidth(window.innerWidth);
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    const isMobile = windowWidth <= 768;

    const handleNewTable = async () => {
        const newTable = manager.createNewTable();
        await manager.createTable(newTable);
        navigate(`/log/${newTable.id}`);
    };

    const openDeleteModal = (id, tableName) => {
        setDeleteModal({ isOpen: true, tableId: id, tableName });
    };

    const closeDeleteModal = () => {
        setDeleteModal({ isOpen: false, tableId: null, tableName: "" });
    };

    const confirmDelete = async () => {
        if (!deleteModal.tableId) return;
        
        try {
            await manager.deleteTable(deleteModal.tableId);
            setTables(prev => prev.filter(table => table.id !== deleteModal.tableId));
            closeDeleteModal();
        } catch (error) {
            console.error('Error deleting table:', error);
            alert('Failed to delete workout. Please try again.');
        }
    };

    const handleExportSingle = async (tableId, tableName) => {
        try {
            const fullTable = await manager.loadTable(tableId);
            const table = tables.find(t => t.id === tableId);
            const workoutData = {
                ...fullTable,
                date: table.date,
                tableName: table.tableName
            };
            await DataExportService.exportWorkoutToCSV(workoutData);
        } catch (error) {
            console.error('Error exporting workout:', error);
            alert('Failed to export workout. Please try again.');
        }
    };

    const handleExportAll = async () => {
        if (tables.length === 0) return;
        
        try {
            const fullWorkouts = await Promise.all(
                tables.map(async (table) => {
                    const fullTable = await manager.loadTable(table.id);
                    return {
                        ...fullTable,
                        date: table.date,
                        tableName: table.tableName
                    };
                })
            );
            await DataExportService.exportAllWorkoutsToCSV(fullWorkouts, 'all_workouts');
        } catch (error) {
            console.error('Error exporting workouts:', error);
            alert('Failed to export workouts. Please try again.');
        }
    };

    const handleExportPDF = async () => {
        if (tables.length === 0) return;
        
        try {
            const fullWorkouts = await Promise.all(
                tables.map(async (table) => {
                    const fullTable = await manager.loadTable(table.id);
                    return {
                        ...fullTable,
                        date: table.date,
                        tableName: table.tableName
                    };
                })
            );
            await DataExportService.exportToPDF(fullWorkouts, 'workout_report');
        } catch (error) {
            console.error('Error exporting to PDF:', error);
            alert('Failed to export to PDF. Please try again.');
        }
    };

    const formatDate = (dateString) => {
        if (!dateString) return "";
        const date = new Date(dateString);
        return date.toLocaleDateString('en-US', { 
            weekday: 'short', 
            year: 'numeric', 
            month: 'short', 
            day: 'numeric' 
        });
    };

    return (
        <div className="saved-tables-container">
            <style jsx>{`
                .saved-tables-container {
                    min-height: ${previewMode ? 'auto' : '100vh'};
                    padding: ${isMobile ? '1rem' : '2rem'};
                    padding-top: ${previewMode ? '1rem' : (isMobile ? '5rem' : '6rem')};
                    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
                    color: white;
                }

                .back-button {
                    position: fixed;
                    top: 1.5rem;
                    left: 1.5rem;
                    z-index: 100;
                    background: rgba(255, 255, 255, 0.1);
                    backdrop-filter: blur(10px);
                    border: 1px solid rgba(255, 255, 255, 0.2);
                    border-radius: 12px;
                    padding: 0.75rem 1.5rem;
                    color: white;
                    font-weight: 600;
                    text-decoration: none;
                    transition: all 0.3s ease;
                    cursor: pointer;
                }

                .back-button:hover {
                    background: rgba(255, 255, 255, 0.2);
                    transform: translateY(-2px);
                }

                .page-header {
                    text-align: center;
                    margin-bottom: 3rem;
                }

                .page-title {
                    font-size: ${isMobile ? '2.5rem' : '3.5rem'};
                    font-weight: 700;
                    margin-bottom: 1rem;
                    background: linear-gradient(45deg, #fff, #e0e7ff);
                    -webkit-background-clip: text;
                    -webkit-text-fill-color: transparent;
                    background-clip: text;
                }

                .create-button {
                    background: linear-gradient(45deg, #10b981, #059669);
                    border: none;
                    border-radius: 16px;
                    padding: 1rem 2rem;
                    color: white;
                    font-weight: 700;
                    font-size: 1.1rem;
                    cursor: pointer;
                    transition: all 0.3s ease;
                    box-shadow: 0 8px 25px rgba(16, 185, 129, 0.3);
                    margin-bottom: 2rem;
                }

                .create-button:hover {
                    transform: translateY(-3px);
                    box-shadow: 0 12px 35px rgba(16, 185, 129, 0.4);
                }

                .export-actions {
                    display: flex;
                    justify-content: center;
                    gap: 1rem;
                    margin-bottom: 3rem;
                    flex-wrap: wrap;
                }

                .export-btn {
                    background: rgba(255, 255, 255, 0.1);
                    backdrop-filter: blur(10px);
                    border: 1px solid rgba(255, 255, 255, 0.2);
                    border-radius: 12px;
                    padding: 0.75rem 1.5rem;
                    color: white;
                    font-weight: 600;
                    cursor: pointer;
                    transition: all 0.3s ease;
                    display: flex;
                    align-items: center;
                    gap: 0.5rem;
                }

                .export-btn:hover {
                    background: rgba(255, 255, 255, 0.2);
                    transform: translateY(-2px);
                }

                .tables-grid {
                    display: grid;
                    grid-template-columns: repeat(auto-fill, minmax(${isMobile ? '280px' : '350px'}, 1fr));
                    gap: 1.5rem;
                    max-width: 1200px;
                    margin: 0 auto;
                }

                .table-card {
                    background: rgba(255, 255, 255, 0.1);
                    backdrop-filter: blur(15px);
                    border: 1px solid rgba(255, 255, 255, 0.2);
                    border-radius: 20px;
                    padding: 1.5rem;
                    transition: all 0.3s ease;
                    cursor: pointer;
                    position: relative;
                    overflow: hidden;
                }

                .table-card::before {
                    content: '';
                    position: absolute;
                    top: 0;
                    left: 0;
                    right: 0;
                    height: 4px;
                    background: linear-gradient(90deg, #10b981, #3b82f6, #8b5cf6);
                }

                .table-card:hover {
                    transform: translateY(-8px);
                    background: rgba(255, 255, 255, 0.15);
                    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.2);
                }

                .card-content {
                    margin-bottom: 1.5rem;
                }

                .table-name {
                    font-size: 1.3rem;
                    font-weight: 700;
                    margin-bottom: 0.5rem;
                    color: white;
                }

                .table-date {
                    color: rgba(255, 255, 255, 0.8);
                    font-size: 0.9rem;
                    margin-bottom: 0.25rem;
                }

                .table-last-opened {
                    color: rgba(255, 255, 255, 0.6);
                    font-size: 0.8rem;
                }

                .card-actions {
                    display: flex;
                    gap: 0.75rem;
                    justify-content: flex-end;
                }

                .action-btn {
                    background: rgba(255, 255, 255, 0.1);
                    border: 1px solid rgba(255, 255, 255, 0.2);
                    border-radius: 8px;
                    padding: 0.5rem 1rem;
                    color: white;
                    font-size: 0.85rem;
                    font-weight: 600;
                    cursor: pointer;
                    transition: all 0.2s ease;
                    display: flex;
                    align-items: center;
                    gap: 0.3rem;
                }

                .action-btn:hover {
                    background: rgba(255, 255, 255, 0.2);
                    transform: translateY(-1px);
                }

                .delete-btn {
                    background: rgba(239, 68, 68, 0.2);
                    border-color: rgba(239, 68, 68, 0.3);
                    color: #fca5a5;
                }

                .delete-btn:hover {
                    background: rgba(239, 68, 68, 0.3);
                    color: white;
                }

                .empty-state {
                    text-align: center;
                    padding: 4rem 2rem;
                    color: rgba(255, 255, 255, 0.7);
                }

                .empty-state h3 {
                    font-size: 1.5rem;
                    margin-bottom: 1rem;
                    color: white;
                }

                .modal-overlay {
                    position: fixed;
                    top: 0;
                    left: 0;
                    right: 0;
                    bottom: 0;
                    background: rgba(0, 0, 0, 0.7);
                    backdrop-filter: blur(5px);
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    z-index: 1000;
                    padding: 1rem;
                }

                .modal {
                    background: white;
                    border-radius: 20px;
                    padding: 2rem;
                    max-width: 400px;
                    width: 100%;
                    box-shadow: 0 25px 50px rgba(0, 0, 0, 0.3);
                    transform: scale(0.9);
                    animation: modalAppear 0.3s ease forwards;
                }

                @keyframes modalAppear {
                    to {
                        transform: scale(1);
                    }
                }

                .modal-header {
                    display: flex;
                    align-items: center;
                    gap: 1rem;
                    margin-bottom: 1.5rem;
                }

                .modal-icon {
                    width: 48px;
                    height: 48px;
                    background: linear-gradient(45deg, #ef4444, #dc2626);
                    border-radius: 50%;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    color: white;
                    font-size: 1.5rem;
                    font-weight: bold;
                }

                .modal-title {
                    font-size: 1.25rem;
                    font-weight: 700;
                    color: #1f2937;
                    margin: 0;
                }

                .modal-body {
                    color: #6b7280;
                    margin-bottom: 2rem;
                    line-height: 1.6;
                }

                .modal-actions {
                    display: flex;
                    gap: 1rem;
                    justify-content: flex-end;
                }

                .modal-btn {
                    padding: 0.75rem 1.5rem;
                    border-radius: 8px;
                    font-weight: 600;
                    cursor: pointer;
                    transition: all 0.2s ease;
                    border: none;
                }

                .modal-btn-cancel {
                    background: #f3f4f6;
                    color: #374151;
                }

                .modal-btn-cancel:hover {
                    background: #e5e7eb;
                }

                .modal-btn-delete {
                    background: #ef4444;
                    color: white;
                }

                .modal-btn-delete:hover {
                    background: #dc2626;
                }
            `}</style>

            {!previewMode && (
                <button className="back-button" onClick={() => navigate("/")}>
                    ‚Üê Dashboard
                </button>
            )}

            {!previewMode && (
                <div className="page-header">
                    <h1 className="page-title">Training Logs</h1>
                    <button className="create-button" onClick={handleNewTable}>
                        + Create New Workout
                    </button>

                    {tables.length > 0 && (
                        <div className="export-actions">
                            <button className="export-btn" onClick={handleExportAll}>
                                üìä Export All CSV
                            </button>
                            <button className="export-btn" onClick={handleExportPDF}>
                                üìÑ Export PDF Report
                            </button>
                        </div>
                    )}
                </div>
            )}

            <div className="content">
                {isLoading ? (
                    <SkeletonLoader type="card" count={3} />
                ) : tables.length === 0 ? (
                    <div className="empty-state">
                        <h3>No workout logs yet</h3>
                        <p>Create your first workout to get started!</p>
                    </div>
                ) : (
                    <div className="tables-grid">
                        {tables.map((table) => (
                            <div
                                key={table.id}
                                className="table-card"
                                onClick={() => navigate(`/log/${table.id}`)}
                            >
                                <div className="card-content">
                                    <h3 className="table-name">{table.tableName}</h3>
                                    <div className="table-date">{formatDate(table.date)}</div>
                                    {table.lastOpened && (
                                        <div className="table-last-opened">
                                            Last opened: {formatDate(table.lastOpened)}
                                        </div>
                                    )}
                                </div>

                                {!previewMode && (
                                    <div className="card-actions">
                                        <button
                                            className="action-btn"
                                            onClick={(e) => {
                                                e.stopPropagation();
                                                handleExportSingle(table.id, table.tableName);
                                            }}
                                        >
                                            üìä Export
                                        </button>
                                        <button
                                            className="action-btn delete-btn"
                                            onClick={(e) => {
                                                e.stopPropagation();
                                                openDeleteModal(table.id, table.tableName);
                                            }}
                                        >
                                            üóë Delete
                                        </button>
                                    </div>
                                )}
                            </div>
                        ))}
                    </div>
                )}
            </div>

            {deleteModal.isOpen && (
                <div className="modal-overlay" onClick={closeDeleteModal}>
                    <div className="modal" onClick={(e) => e.stopPropagation()}>
                        <div className="modal-header">
                            <div className="modal-icon">!</div>
                            <h3 className="modal-title">Delete Workout</h3>
                        </div>
                        <div className="modal-body">
                            Are you sure you want to delete <strong>"{deleteModal.tableName}"</strong>?
                            <br />
                            This action cannot be undone and all workout data will be permanently removed.
                        </div>
                        <div className="modal-actions">
                            <button className="modal-btn modal-btn-cancel" onClick={closeDeleteModal}>
                                Cancel
                            </button>
                            <button className="modal-btn modal-btn-delete" onClick={confirmDelete}>
                                Delete Workout
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}
